import React, { useState } from 'react';
import  HeaderAluno  from '../../components/headeraluno';
import  FooterAluno  from '../../components/footeraluno';
import {  Container, Form, Button } from 'react-bootstrap';
import './index.css';

const Login = () => {

  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const logar = (event) => {
    event.preventDefault();

    console.log('${email} - ${senha}');
  }
    return (
      <div>
          <HeaderAluno/>
          <Container>
          <Form className='form-signin'>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Email </Form.Label>
              <Form.Control type="email" value={email} onChange={ event => setEmail(event)} placeholder="Coloque seu email" />
              <Form.Text className="text-muted">
              </Form.Text>
            </Form.Group>
            
            <Form.Group controlId="formBasicPassword">
              <Form.Label>Senha</Form.Label>
              <Form.Control type="password" value={senha} placeholder="Senha" />
            </Form.Group>
            
            <Form.Group controlId="formBasicCheckbox">
              <Form.Check type="checkbox" label="Manter Login" />
            </Form.Group>

            <Button variant="dark" type="submit">
                Logar
            </Button>
          </Form>
         </Container>
        < FooterAluno/>
    </div>
)
}
export default Login;
